
-- Table: abilities
CREATE TABLE abilities ( 
    id             INTEGER        NOT NULL,
    identifier     VARCHAR( 79 )  NOT NULL,
    generation_id  INTEGER        NOT NULL,
    is_main_series BOOLEAN        NOT NULL,
    PRIMARY KEY ( id ),
    FOREIGN KEY ( generation_id ) REFERENCES generations ( id ),
    CHECK ( is_main_series IN ( 0, 1 )  ) 
);

