insert into pokemon_move_method_prose ("pokemon_move_method_id", "local_language_id", "name", "description") values ('5', '9', 'Stadium: Surfing Pikachu', 'Learned when a non-rental Pikachu helps beat Prime Cup Master Ball R-2.  It must participate in every battle, and you must win with no continues.');
